mapper.select:
SELECT SPR.MGR_ID, SPR.USER_CODE, SPR.MGR_NAME, SPR.MGR_TYPE, SPR.ORG_ID, SPR. PASSWORD, SPR.END_DATE, SPR.START_DATE, SPR. STOP, SPR.LOGIN_TIMES, SPR.LAST_LOGIN_DATE, SPR.LOGIN_DATE, SPR.GRANT_TYPE, SPR.IS_SUPER, SPR. STATUS, SPR.REMARK, SPR.UPDATED_DT, SPR.UPDATED_BY, SPR.CREATED_DT, SPR.CREATED_BY, SPR.IS_DELETED, SPR.VERSION, SPR.MAIL, SR.ROLE_NAME FROM STBL_PRI_MANAGER SPR LEFT JOIN STBL_PRI_USER_ROLE SPUR ON SPR.MGR_ID = SPUR.MGR_ID AND SPUR.IS_DELETED = 'N' LEFT JOIN STBL_PRI_ROLE SR ON SPUR.ROLE_ID = SR.ROLE_ID AND SR.IS_DELETED = 'N' WHERE SPR.IS_DELETED = 'N'
AND SPR.USER_CODE = #{userCode}
AND SPR.PASSWORD = #{password}

mapper.update:
update STBL_PRI_MANAGER
	SET UPDATED_DT=now(),
		  VERSION=VERSION+1

			, USER_CODE = #{userCode}


			, MGR_NAME = #{mgrName}


			, MGR_TYPE = #{mgrType}


			, ORG_ID = #{orgId}


			, TOP_PARENT_ID = #{topParentId}


			, PASSWORD = #{password}


			, MOBILE = #{mobile}


			, E_MAIL = #{email}


			, remark = #{remark}

	     where MGR_ID = #{mgrId}

UPDATE   STBL_PRI_MANAGER
   SET   STOP = (CASE STOP WHEN '1' THEN '0' ELSE '1' END)
 WHERE   MGR_ID = #{mgrId}

mapper.select:
select c.id from c_organization c
	start with c.parent_id = #{parentId}
	connect by prior c.id = c.parent_id
