mapper.select:
SELECT id id,parentId parentId,title title, href href,icon icon,rightIcon rightIcon, target target,level level,code code, ord ord,isleaf isLeaf,description description from ods_s_menu t where 1=1
and parentId = #{PARENT_ID}
order by ord

mapper.insert:

  insert into ods_s_menu(parentid, title, href,
				icon, rightIcon,target, level,
			ord,code,isleaf,description) 
	values (
			'1', #{title}, #{href},
			'fa fa-tachometer',#{rightIcon}, #{target}, 2,
			#{ord}, #{code},#{isLeaf},#{description}
);

mapper.update:
update ods_s_menu t set 

    t.title = #{title}


    ,t.code = #{code}


    ,t.href = #{href}


    ,t.ord = #{ord}


    ,t.parentId = #{parentId}


    ,t.isLeaf = #{isLeaf}


    , t.description = #{description}

    where t.id = #{id}
