mapper.select:
select id id, name name, code code, phone phone
, active_flag activeFlag, is_manager manager,passWord passWord from ods_s_user t where 1=1 
	and t.code = #{code}

mapper.update:
update ods_s_user t set 

			t.name = #{name}


			,t.code = #{code}


			, t.phone = #{phone}


			, t.descrition = #{descrition}


			, t.e_mail = #{email}

	     where t.id = #{id}
